<?php/** * Interface iMessage websocket 控制器继承接口 *   1 = HEART		  	   // 心跳 *   2 = LOGIN			   // 登录 *   3 = LOGOUT			   // 退出登录 *   4 = COMMENT		       // 评论 *   5 = PULL		       // 拉取 *   6 = NOTIFICATION	   // 通知 *   7 = READ			   // 已读 */namespace App\Library\IMessage;interface iMessageController {    //心跳 HEART 1    function heart();    //登陆 LOGIN 2    function login();    //退出登录 LOGOUT 3    function logout();    //拉取 PULL 5    function pull();    //已读 READ 7    function read();    //收到确认 RECEIVED 8    function received();}